    suspend fun moveNote(target: Int, from: Int, to: Int) {
        Timber.d("moveNote $target $from $to")
        moveNotes(
            target,
            if (from > to) 1 else -1,
            min(from, to),
            max(from, to)
        )
        updateOrder(target, to - from)
