fun findAll(playerIds: List<String>): List<Player> {
        val playerJobs = playerIds
            .map {
                GlobalScope.async(Dispatchers.IO) {
                    collectionReference
                        .document(it)
                        .getSync()
                }
            }

        return runBlocking {
            playerJobs.map {
                val doc = it.await()
                toEntityObject(doc.data!!)
            }
        }
    }

