    @Transaction
    fun insert(vararg entitlements: Entitlement) {
        entitlements.forEach {
            when (it) {
                is GasTank -> insert(it)
                is PremiumCar -> insert(it)
                is GoldStatus -> insert(it)
            }
        }
    }

