override suspend fun updateOrInsertInternal(item: WatchlistItem): Long {
        if (item.id != 0L) {
            update(item)
        } else {
            val existingEvent = getItemForGuid(item.eventGuid)
            if (existingEvent != null) {
                item.id = existingEvent.id
                update(item)
            } else {
                item.id = insert(item)
            }
        }
        return item.id
    }

