override suspend fun updateOrInsertInternal(item: RelatedEvent): Long {
        if (item.id != 0L) {
            update(item)
        } else {
            val existingItem = findSpecificRelatedEvent(item.parentEventId, item.relatedEventGuid)
            if (existingItem != null) {
                item.id = existingItem.id
                update(item)
            } else {
                item.id = insert(item)
            }
        }
        return item.id
    }

