        val currentDateMillis = afterTime.toLocalDate().startOfDayUTC()

        val millisOfDay = afterTime.toLocalTime().toSecondOfDay().seconds.millisValue

        val r = dao.findAfter(currentDateMillis, millisOfDay) ?: return null

